// SideMenu.js
import React, { useState } from 'react';
import './SideMenu.css';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlay, faTimes } from '@fortawesome/free-solid-svg-icons';


const SideMenu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      <div className="menu-icon" onClick={toggleMenu}>
        {isOpen ? <FontAwesomeIcon icon={faBars} className="Header__menu-icon" /> : <FontAwesomeIcon icon={faBars} className="Header__menu-icon" />}
      </div>
      <div className={`side-menu ${isOpen ? 'open' : ''}`}>
        <ul>
          <li>
            <Link to="/informacije" onClick={toggleMenu}>Informacije</Link>
          </li>
          <li>
            <Link to="/kursevi" onClick={toggleMenu}>Kursevi</Link>
          </li>
        </ul>
      </div>
    </>
  );
};

export default SideMenu;
