import React, { useState } from 'react'
import "./PageList.css" 


const PageList = ({item, isActive, onClick}) => {
  const [active, setActive] = useState(false);

  const handleActive = () => { 
    setActive(true);
  } 

  const handleNotActive = () => { 
    setActive(false);
  } 


  return (
    <a  onMouseEnter={handleActive} 
        onMouseLeave={handleNotActive} href={item.path} 
        onClick={onClick}
        className={(isActive || active) ? "PageList__container activePageList" : 'PageList__container'} 
    >
       {item.name}
    </a>
  )
}

export default PageList
