import React, { useEffect } from 'react'
import "./SingleCoursePage.css" 
import CourseInfoCard from '../../components/CourseInfoComponent/CourseInfoCard';
import { Link, useLocation, useParams } from 'react-router-dom';
import pozadina from "../../images/pozadina.webp"
import axios from 'axios';
import api from '../../services/api';

const SingleCoursePage = () => {

  const courseId = useParams();
  const [course, setCourse] = useState("");
  const [error, setError] = useState('');

  const courseData = {
      title1: 'Math Beasts Camp 3: Logic Training in Puzzles and Games',
      description: 'Math Beasts Camp 3: Shapes, Patterns, and Creativity',
      grade: 3,
      term: 'Summer',
      length: '2 weeks',
      dailyInClassTime: '1.5 hours',
      tuition: '$399 (~$27 per hour)',
      skills: ['Pattern recognition', 'cryptography', 'problem solving', "geometry", "number sense"]
  }; 


  useEffect(() => {  


      window.scrollTo(0,0); 
      try { 
          const response = axios.get(`${api.API_URL}/courses/${courseId}/`)

      } 
      catch { 
        console.log("error"); 
      }



  }, [])

  return (
    <div className='SingleCoursePage__container'>
        <img className='SingleCoursePage__container-bg' src={pozadina} alt="" />
        <CourseInfoCard course={courseData} />

        <div className='SingleCoursePage__container-right'>
            <h2>{courseData.title1}</h2>
            <p>{courseData.description}</p>
            <Link to="https://www.google.com" className='SingleCoursePage__link'>Prijavi Se</Link>
        </div>


    </div>
  )
}

export default SingleCoursePage
