import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './MenuItem.css';

const MenuItem = ({ title, path, subMenu }) => {
  const [isSubMenuOpen, setIsSubMenuOpen] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const location = useLocation();

  useEffect(() => {
    if (subMenu && subMenu.length > 0) {
      const isSubMenuActive = subMenu.some(subItem => location.pathname === subItem.path);
      setIsActive(isSubMenuActive);
    } else {
      setIsActive(location.pathname === path);
    }
  }, [location, path, subMenu]);

  const handleMouseEnter = () => {
    setIsSubMenuOpen(true);
  };

  const handleMouseLeave = () => {
    setIsSubMenuOpen(false);
  };

  return (
    <div
      className='MenuItem__container'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <Link to={path} className={isActive ? 'active' : ''}>{title}</Link>
      {subMenu && subMenu.length > 0 && (
        <div className={`SubMenu ${isSubMenuOpen ? 'open' : ''}`}>
          {subMenu.map((subItem, index) => (
            <Link key={index} to={subItem.path} className='SubMenuItem'>
              {subItem.name}
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default MenuItem;
