import React from 'react'
import "./Header.css" 
import sigma from "../../images/sigma.png" 
import Menu from '../Menu/Menu'
import { Link } from 'react-router-dom' 
import { useEffect, useContext } from 'react' 
import AuthContext from '../AuthContext/AuthContext'

const Header = () => {

    const menuItems = [
        {
          name: 'Informacije',
          path: '/sigma', 
          subMenu: [
            { name: 'Pregled', path: '/sigma/pregled' },
            { name: 'Detalji', path: '/sigma/detalji' },
            { name: 'Česta Pitanja', path: '/sigma/faq' }
          ]
        },
        {
          name: 'Kursevi',
          path: '/kursevi', 
          subMenu: [
            { name: 'Pregled', path: '/sigma/pregled' },
            { name: 'Detalji', path: '/sigma/detalji' },
            { name: 'Česta Pitanja', path: '/sigma/faq' }
          ]
        }
      ];

      const {setScrolled} = useContext(AuthContext); 
      let {scrolled} = useContext(AuthContext);

      const handleScroll = () => {
        if (window.scrollY > 50) {
          setScrolled(true);
        } else {
          setScrolled(false);
        }
      };

      useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
          window.removeEventListener('scroll', handleScroll);
        };
      }, []);

  return (

    <div className={ scrolled ? "Header__container menu-active" : 'Header__container'}>
        <div className='Header__container-help'>
            <div className='Header__container-left'> 
                <img className={!scrolled ? "img__not-scaled" : "img__scaled"} src={sigma} alt="" />
                <p>Sigma <span>Academy</span></p>
            </div>

            <div className='Header__container-right'>
                {!scrolled && <Link to={"/login"}>Login</Link> } 
                <Menu menuItems={menuItems} />
            </div>

        </div>

    </div>
  )
}

export default Header
