import React, { useContext, useState } from 'react'
import "./PageList.css" 
import { AuthProvider } from '../AuthContext/AuthContext';


const PageList = ({item, isActive, onClick}) => {
  const [active, setActive] = useState(false);
  const { activeIndex, setActiveIndex } = useContext(AuthProvider);
  const handleActive = () => { 
    setActive(true);
  } 

  const handleNotActive = () => { 
    setActive(false);
  } 


  return (
    <a  onMouseEnter={handleActive} 
        onMouseLeave={handleNotActive} href={item.path} 
        onClick={onClick}
        className={(isActive) ? "PageList__container activePageList" : 'PageList__container'}>
       {item.name}
    </a>
  )
}

export default PageList
