import axios from 'axios';
import api from '../services/api'

const API_URL = 'http://127.0.0.1:8000/api'; // Replace with your API URL


const getToken = () => {
    return localStorage.getItem('access_token');
  };


  const getUserProfile = async () => {
    const token = getToken();
    if (!token) {
      return null;
    }

    try {
      const response = await axios.get(`${API_URL}/profile/`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      return response.data;
    } catch (error) {
      if (error.response && error.response.status === 401) {
        handleLogout();
      }
      throw error;
    }
  };


const login = async (username, password) => {
    try {
        const response = await axios.post(`${API_URL}/token/`, {
            username,
            password,
        });
        localStorage.setItem('access_token', response.data.access);
        localStorage.setItem('refresh_token', response.data.refresh);
        // Redirect to home or dashboard page
        // window.location.href = '/'; // Change to your desired route
    } catch (error) {
        if (error.response) {
        // Server responded with a status other than 200 range
        throw new Error('Invalid username or password');
        } else if (error.request) {
        // Request was made but no response was received
        throw new Error('Network error, please try again later');
        } else {
        // Something happened in setting up the request that triggered an Error
        throw new Error('An error occurred, please try again later');
        }
    }
    };


const handleLogout = async () => {
    // Remove tokens from localStorage
    localStorage.removeItem('access_token');
    localStorage.removeItem('refresh_token');
    localStorage.removeItem('user');
    const refreshToken = localStorage.getItem('refresh_token');

    //  inform the server
    try {
        await axios.post(`${API_URL}/api/logout/`, { refresh_token: refreshToken });
    } catch (error) {
        console.error('Error during logout: ', error);
    }



    // Redirect to login page or home page
    window.location.href = '/login'; // or any other route
};



export default {
    getUserProfile, 
    login,
    handleLogout, 
};
