import React, { useState, useEffect } from 'react';
import "./CoursesPage.css";
import CourseCard from '../../components/CourseCard/CourseCard';
import pozadina from "../../images/pozadina.webp";
import api from '../../services/api';

const CoursesPage = () => {
  const [courses, setCourses] = useState([]);
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const data = await api.getAllCourses();
        setCourses(data);
      } catch (err) {
        setError('Failed to fetch courses');
      }
    };

    fetchCourses();
  }, []);

  const handleFilterChange = async (event) => {
    const value = event.target.value;
    const newSelectedFilters = selectedFilters.includes(value)
      ? selectedFilters.filter((filter) => filter !== value)
      : [...selectedFilters, value];

    setSelectedFilters(newSelectedFilters);

    if (newSelectedFilters.length === 0) {
      try {
        const data = await api.getAllCourses();
        setCourses(data);
      } catch (err) {
        setError('Failed to fetch courses');
      }
    } else {
      try {
        const data = await api.searchCoursesByCategory(newSelectedFilters);
        setCourses(data);
      } catch (err) {
        setError('Failed to fetch filtered courses');
      }
    }
  };

  return (
    <div className='CoursesPage__container'>
      <img className='CoursePage__bg' src={pozadina} alt="" />
      <div className='CoursesPage__container-right'>
        <h1>Na≈°i Kursevi</h1>
        <p>In instructor-led classes, your student will develop problem-solving and communication skills that will help them succeed in school and beyond. An interactive online platform complements live instruction from subject experts.</p>

        <div className='CoursesPage__filter-container'>
          <label className="filter-button">
            <input type="checkbox" name="filter" value="Matematika Osnovna" onChange={handleFilterChange} />
            <span>Matematika Osnovna</span>
          </label>

          <label className="filter-button">
            <input type="checkbox" name="filter" value="Fizika Osnovna" onChange={handleFilterChange} />
            <span>Fizika Osnovna</span>
          </label>

          <label className="filter-button">
            <input type="checkbox" name="filter" value="Matematika Srednja" onChange={handleFilterChange} />
            <span>Matematika Srednja</span>
          </label>

          <label className="filter-button">
            <input type="checkbox" name="filter" value="Fizika Srednja" onChange={handleFilterChange} />
            <span>Fizika Srednja</span>
          </label>
        </div>

        <div className='CourseDiv__container'>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {courses.map((course) => (
            <CourseCard key={course.id} item={course} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default CoursesPage;
