import React, { useState } from 'react';
import "./CoursesPage.css";
import PageList from '../../components/PageList/PageList';
import CourseCard from '../../components/CourseCard/CourseCard';
import pozadina from "../../images/pozadina.webp";

const CoursesPage = () => {

  const menuItems = [
    {
      name: 'Informacije',
      path: '/informacije',
    },
    {
      name: 'Kursevi',
      path: '/kursevi',
    }
  ];

  const courses = [
    {
      name: "Matematika",
      list: [
        {
          id: 1,
          title: "Osnovi Algebra",
          description: "Naučite osnovne koncepte algebre, uključujući jednadžbe i nejednadžbe.",
          instructor: "Marko Marković",
          startDate: "2024-07-01",
          duration: "8 nedelja",
          level: "Početni",
          price: "150 EUR",
          category: "Matematika Osnovna"
        },
        {
          id: 2,
          title: "Napredna Geometrija",
          description: "Produbite svoje znanje geometrije uz napredne teoreme i prakse.",
          instructor: "Jelena Janković",
          startDate: "2024-08-15",
          duration: "10 nedelja",
          level: "Napredni",
          price: "200 EUR",
          category: "Matematika Srednja"
        }, 
        {
          id: 2,
          title: "Napredna Geometrija",
          description: "Produbite svoje znanje geometrije uz napredne teoreme i prakse.",
          instructor: "Jelena Janković",
          startDate: "2024-08-15",
          duration: "10 nedelja",
          level: "Napredni",
          price: "200 EUR",
          category: "Matematika Srednja"
        }, 
        {
          id: 2,
          title: "Napredna Geometrija",
          description: "Produbite svoje znanje geometrije uz napredne teoreme i prakse.",
          instructor: "Jelena Janković",
          startDate: "2024-08-15",
          duration: "10 nedelja",
          level: "Napredni",
          price: "200 EUR",
          category: "Matematika Srednja"
        }
      ],
    },
    {
      name: "Fizika",
      list: [
        {
          id: 3,
          title: "Osnovi Mehanike",
          description: "Upoznajte se sa osnovnim konceptima mehanike, uključujući kinematiku i dinamiku.",
          instructor: "Ana Aničić",
          startDate: "2024-09-01",
          duration: "8 nedelja",
          level: "Početni",
          price: "180 EUR",
          category: "Fizika Osnovna"
        },
        {
          id: 4,
          title: "Elektromagnetizam",
          description: "Naučite osnove elektromagnetizma, uključujući električna polja i magnetizam.",
          instructor: "Petar Petrović",
          startDate: "2024-10-01",
          duration: "12 nedelja",
          level: "Srednji",
          price: "220 EUR",
          category: "Fizika Srednja"
        }, 
        {
          id: 4,
          title: "Elektromagnetizam",
          description: "Naučite osnove elektromagnetizma, uključujući električna polja i magnetizam.",
          instructor: "Petar Petrović",
          startDate: "2024-10-01",
          duration: "12 nedelja",
          level: "Srednji",
          price: "220 EUR",
          category: "Fizika Srednja"
        }, 
        {
          id: 4,
          title: "Elektromagnetizam",
          description: "Naučite osnove elektromagnetizma, uključujući električna polja i magnetizam.",
          instructor: "Petar Petrović",
          startDate: "2024-10-01",
          duration: "12 nedelja",
          level: "Srednji",
          price: "220 EUR",
          category: "Fizika Srednja"
        }
      ]
    }
  ];

  const [selectedFilters, setSelectedFilters] = useState([]);


  const handleFilterChange = (event) => {
    const value = event.target.value;
    setSelectedFilters((prevFilters) =>
      prevFilters.includes(value)
        ? prevFilters.filter((filter) => filter !== value)
        : [...prevFilters, value]
    );
  };


  const filteredCourses = courses.map((courseCategory) => ({
    ...courseCategory,
    list: courseCategory.list.filter((course) =>
      selectedFilters.length === 0 || selectedFilters.includes(course.category)
    )
  }));


  return (
    <div className='CoursesPage__container'>

      <img className='CoursePage__bg' src={pozadina} alt="" />

      <div className='CoursesPage__container-right'>
        <h1>Naši Kursevi</h1>
        <p>In instructor-led classes, your student will develop problem-solving and communication skills that will help them succeed in school and beyond. An interactive online platform complements live instruction from subject experts.</p>

        <div className='CoursesPage__filter-container'>
          <label className="filter-button">
            <input type="checkbox" name="filter" value="Matematika Osnovna" onChange={handleFilterChange} />
            <span>Matematika Osnovna</span>
          </label>

          <label className="filter-button">
            <input type="checkbox" name="filter" value="Fizika Osnovna" onChange={handleFilterChange} />
            <span>Fizika Osnovna</span>
          </label>

          <label className="filter-button">
            <input type="checkbox" name="filter" value="Matematika Srednja" onChange={handleFilterChange} />
            <span>Matematika Srednja</span>
          </label>

          <label className="filter-button">
            <input type="checkbox" name="filter" value="Fizika Srednja" onChange={handleFilterChange} />
            <span>Fizika Srednja</span>
          </label>
        </div>

        <div className='CourseDiv__container'>
          {filteredCourses.map((courseCategory, index) => (
            <div key={index} className='CourseDiv__category-container'>
              <h2>{courseCategory.name}</h2>
              <div className='CourseDiv__CardContainer'>
                {courseCategory.list.map((course) => (
                  <CourseCard key={course.id} item={course} />
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CoursesPage;
